import React,{ useState } from "react";
import TareaFormulario from "./Formulario";
import '../hojas de estilo/ListaDeTareas.css';
import Tarea from './Tarea';

function ListadoDeTareas(){

  const[tarea, setTareas] = useState([]);

  const agregarTarea = tarea =>{
    console.log("tarea");
    
    if(tarea.texto.trim()){
      tarea.texto = tarea.texto.trim()
      const tareasActualizadas = [tarea, ...tarea];
      setTareas(tareasActualizadas)

    }
  };

    return(
        <>
        <TareaFormulario  onSumit={agregarTarea} />
        <div className="tareas-lista-contenedor">
          {
            tareas.map((tarea)=>
            <Tarea
            key={tarea.id}
            id={tarea.id} 
            texto={tarea.texto}
            completado={tarea.completada}

            />

            )
          }
        </div>
        </>
    );
}

export default ListadoDeTareas;
